import React, { useState } from "react";
import styled from "styled-components";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

const Container = styled.div`
  background: ${({ theme }) => theme.background};
  color: ${({ theme }) => theme.text};
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;
const Input = styled.input`
  background: #fff;
  color: #000;
  border: 1px solid #ccc;
  padding: 10px;
  margin: 8px 0;
  border-radius: 4px;
`;
const Button = styled.button`
  background: ${({ theme }) => theme.accent};
  color: #fff;
  border: none;
  padding: 10px 20px;
  border-radius: 4px;
  margin-top: 10px;
  cursor: pointer;
`;
const Error = styled.div`
  color: ${({ theme }) => theme.accent};
  margin-bottom: 10px;
`;
const Form = styled.form`
  display: flex;
  flex-direction: column;
  align-items: stretch;
  width: 300px;
`;

function Register() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [rePassword, setRePassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [showRePassword, setShowRePassword] = useState(false);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    // Username validation: must not start with a number and not be all numbers
    if (/^\d/.test(name)) {
      setError("Username must not start with a number.");
      return;
    }
    if (/^\d+$/.test(name)) {
      setError("Username cannot be all numbers.");
      return;
    }
    // Email validation: must not start with a number and not be all numbers
    if (/^\d/.test(email)) {
      setError("Email must not start with a number and must be a valid email address.");
      return;
    }
    if (/^\d+$/.test(email)) {
      setError("Email cannot be all numbers and must be a valid email address.");
      return;
    }
    // Email format validation
    if (!/^\S+@\S+\.\S+$/.test(email)) {
      setError("Please enter a valid email address.");
      return;
    }
    // Password match validation
    if (password !== rePassword) {
      setError("Passwords do not match.");
      return;
    }
    try {
      await axios.post("/api/register", { name, email, password });
      navigate("/risk-profile");
    } catch (err) {
      setError(err.response?.data?.error || "Registration failed");
    }
  };

  return (
    <Container>
      <h2>Register</h2>
      {error && <Error>{error}</Error>}
      <Form onSubmit={handleSubmit}>
        <Input
          type="text"
          placeholder="Name"
          value={name}
          onChange={e => setName(e.target.value)}
          required
        />
        <Input
          type="email"
          placeholder="Email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          required
        />
        <div style={{ position: 'relative' }}>
          <Input
            type={showPassword ? "text" : "password"}
            placeholder="Password"
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
            style={{ paddingRight: 36 }}
          />
          <span
            onClick={() => setShowPassword(v => !v)}
            style={{ position: 'absolute', right: 10, top: 14, cursor: 'pointer', userSelect: 'none' }}
            tabIndex={0}
            aria-label={showPassword ? 'Hide password' : 'Show password'}
          >
            {showPassword ? (
              <svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M10 4C5 4 1.73 8.11 1.73 8.11C1.38 8.53 1.38 9.17 1.73 9.59C1.73 9.59 5 14 10 14C15 14 18.27 9.89 18.27 9.89C18.62 9.47 18.62 8.83 18.27 8.41C18.27 8.41 15 4 10 4ZM10 12C7.24 12 5 9.76 5 7C5 4.24 7.24 2 10 2C12.76 2 15 4.24 15 7C15 9.76 12.76 12 10 12Z" fill="#888"/></svg>
            ) : (
              <svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M1 1L19 19M10 4C5 4 1.73 8.11 1.73 8.11C1.38 8.53 1.38 9.17 1.73 9.59C1.73 9.59 5 14 10 14C12.13 14 14.09 13.13 15.66 11.66M5 7C5 4.24 7.24 2 10 2C12.76 2 15 4.24 15 7C15 8.13 14.5 9.13 13.66 9.88" stroke="#888" strokeWidth="2"/></svg>
            )}
          </span>
        </div>
        <div style={{ position: 'relative' }}>
          <Input
            type={showRePassword ? "text" : "password"}
            placeholder="Re-enter Password"
            value={rePassword}
            onChange={e => setRePassword(e.target.value)}
            required
            style={{ paddingRight: 36 }}
          />
          <span
            onClick={() => setShowRePassword(v => !v)}
            style={{ position: 'absolute', right: 10, top: 14, cursor: 'pointer', userSelect: 'none' }}
            tabIndex={0}
            aria-label={showRePassword ? 'Hide password' : 'Show password'}
          >
            {showRePassword ? (
              <svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M10 4C5 4 1.73 8.11 1.73 8.11C1.38 8.53 1.38 9.17 1.73 9.59C1.73 9.59 5 14 10 14C15 14 18.27 9.89 18.27 9.89C18.62 9.47 18.62 8.83 18.27 8.41C18.27 8.41 15 4 10 4ZM10 12C7.24 12 5 9.76 5 7C5 4.24 7.24 2 10 2C12.76 2 15 4.24 15 7C15 9.76 12.76 12 10 12Z" fill="#888"/></svg>
            ) : (
              <svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M1 1L19 19M10 4C5 4 1.73 8.11 1.73 8.11C1.38 8.53 1.38 9.17 1.73 9.59C1.73 9.59 5 14 10 14C12.13 14 14.09 13.13 15.66 11.66M5 7C5 4.24 7.24 2 10 2C12.76 2 15 4.24 15 7C15 8.13 14.5 9.13 13.66 9.88" stroke="#888" strokeWidth="2"/></svg>
            )}
          </span>
        </div>
        <Button type="submit">Register</Button>
      </Form>
      <p style={{ color: "#000", marginTop: 16 }}>
        Already have an account? <Link to="/login" style={{ color: "#1976d2" }}>Login here</Link>
      </p>
    </Container>
  );
}

export default Register;
