import React, { useEffect, useState, useRef } from "react";
import styled from "styled-components";
import axios from "axios";
import NavBar from "../components/NavBar";

const Container = styled.div`
  background: ${({ theme }) => theme.background};
  color: ${({ theme }) => theme.text};
  min-height: 100vh;
  padding: 32px;
`;
const Card = styled.div`
  background: ${({ theme }) => theme.card};
  padding: 24px;
  border-radius: 8px;
  margin-bottom: 24px;
  box-shadow: 0 2px 8px #0008;
`;
const Table = styled.table`
  width: 100%;
  background: ${({ theme }) => theme.card};
  color: ${({ theme }) => theme.text};
  border-radius: 8px;
  border-collapse: collapse;
`;
const Th = styled.th`
  border-bottom: 1px solid #fff;
  padding: 12px;
`;
const Td = styled.td`
  padding: 12px;
  border-bottom: 1px solid #fff;
`;
const Button = styled.button`
  background: ${({ theme }) => theme.accent};
  color: #fff;
  border: none;
  padding: 10px 20px;
  border-radius: 4px;
  margin-top: 10px;
  cursor: pointer;
`;

function formatDate(date) {
  if (!date) return "-";
  try {
    const d = typeof date === 'string' ? new Date(date.replace(/Z?$/, 'Z')) : new Date(date);
    if (isNaN(d.getTime())) return "-";
    return d.toLocaleDateString();
  } catch {
    return "-";
  }
}

function Investment() {
  const [type, setType] = useState("Stock");
  const [amount, setAmount] = useState("");
  const [expectedReturn, setExpectedReturn] = useState("");
  const [investments, setInvestments] = useState([]);
  const [msg, setMsg] = useState("");
  const [options, setOptions] = useState([]);
  const [selectedCompany, setSelectedCompany] = useState("");
  const [search, setSearch] = useState("");
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const searchRef = useRef();

  const fetchInvestments = async () => {
    const res = await axios.get("/api/investments", {
      headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
    });
    setInvestments(res.data);
  };

  const fetchOptions = async (t) => {
    const res = await axios.get(`/api/investment-options?type=${t}`);
    setOptions(res.data);
    setSelectedCompany("");
    setExpectedReturn("");
  };

  useEffect(() => {
    fetchInvestments();
    fetchOptions(type);
  }, []);

  useEffect(() => {
    fetchOptions(type);
  }, [type]);

  useEffect(() => {
    if (search.length < 2) {
      setSuggestions([]);
      return;
    }
    let cancel = false;
    axios.get(`/api/search-securities?q=${encodeURIComponent(search)}`)
      .then(res => {
        if (!cancel) setSuggestions(res.data || []);
      })
      .catch(() => setSuggestions([]));
    return () => { cancel = true; };
  }, [search]);

  useEffect(() => {
    function handleClick(e) {
      if (searchRef.current && !searchRef.current.contains(e.target)) {
        setShowSuggestions(false);
      }
    }
    document.addEventListener("mousedown", handleClick);
    return () => document.removeEventListener("mousedown", handleClick);
  }, []);

  const handleCompanyChange = (e) => {
    const val = e.target.value;
    setSelectedCompany(val);
    const opt = options.find(o => o.name === val);
    if (opt) setExpectedReturn(opt.expected_return !== undefined ? String(opt.expected_return) : "");
    else setExpectedReturn("");
  };

  const handleSuggestionClick = (sugg) => {
    if (!options.some(o => o.name === sugg.name)) {
      setOptions(prev => [
        ...prev,
        {
          investment_id: sugg.symbol,
          name: sugg.name,
          type: sugg.type,
          risk: "Medium",
          expected_return: "N/A"
        }
      ]);
    }
    setSelectedCompany(sugg.name);
    setShowSuggestions(false);
    setSearch("");
  };

  const handleAdd = async (e) => {
    e.preventDefault();
    setMsg("");
    try {
      await axios.post("/api/investments", {
        type,
        company: selectedCompany,
        amount: Number(amount),
        expected_return: Number(expectedReturn)
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
      });
      setMsg("Investment added");
      setAmount("");
      setExpectedReturn("");
      setSelectedCompany("");
      fetchInvestments();
    } catch {
      setMsg("Failed to add investment");
    }
  };

  const handleSell = async (id) => {
    try {
      await axios.post(`/api/investments/${id}/sell`, {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
      });
      setMsg("Investment sold");
      fetchInvestments();
    } catch {
      setMsg("Failed to sell investment");
    }
  };

  return (
    <>
      <NavBar />
      <Container>
        <Card>
          <h2>Virtual Investments</h2>
          <form onSubmit={handleAdd} autoComplete="off">
            <select value={type} onChange={e => setType(e.target.value)} style={{ padding: 8, borderRadius: 4, border: '1px solid #fff', background: '#fff', color: '#000' }}>
              <option value="Stock">Stock</option>
              <option value="Mutual Fund">Mutual Fund</option>
            </select>
            <span ref={searchRef} style={{ position: "relative", display: "inline-block", marginLeft: 8, marginRight: 8 }}>
              <input
                type="text"
                placeholder="Search stocks or funds..."
                value={search}
                onChange={e => { setSearch(e.target.value); setShowSuggestions(true); }}
                style={{ padding: 8, borderRadius: 4, border: '1px solid #fff', background: '#fff', color: '#000', width: 200 }}
                onFocus={() => setShowSuggestions(true)}
              />
              {showSuggestions && suggestions.length > 0 && (
                <div style={{ position: "absolute", top: 36, left: 0, background: "#fff", color: "#000", border: "1px solid #ccc", borderRadius: 4, zIndex: 10, width: 300, maxHeight: 200, overflowY: "auto" }}>
                  {suggestions.map((sugg, i) => (
                    <div key={i} style={{ padding: 8, cursor: "pointer" }} onClick={() => handleSuggestionClick(sugg)}>
                      <b>{sugg.symbol}</b> - {sugg.name} <span style={{ color: '#888', fontSize: 12 }}>({sugg.type})</span>
                    </div>
                  ))}
                </div>
              )}
            </span>
            <select value={selectedCompany} onChange={handleCompanyChange} style={{ marginRight: 8, padding: 8, borderRadius: 4, border: '1px solid #fff', background: '#fff', color: '#000' }}>
              <option value="">Select Company</option>
              {options.map(opt => (
                <option key={opt.investment_id} value={opt.name}>{opt.name}</option>
              ))}
            </select>
            <input
              type="number"
              placeholder="Amount"
              value={amount}
              onChange={e => setAmount(e.target.value)}
              required
              style={{ marginRight: 8, padding: 8, borderRadius: 4, border: '1px solid #fff', background: '#fff', color: '#000' }}
            />
            <input
              type="number"
              placeholder="Expected Return %"
              value={expectedReturn}
              onChange={e => setExpectedReturn(e.target.value)}
              required
              style={{ marginRight: 8, padding: 8, borderRadius: 4, border: '1px solid #fff', background: '#fff', color: '#000' }}
              readOnly
            />
            <Button type="submit">Add Investment</Button>
          </form>
          {msg && <div style={{ color: "#e53935", marginTop: 8 }}>{msg}</div>}
        </Card>
        <Card>
          <h3>Current Investments</h3>
          <Table>
            <thead>
              <tr>
                <Th>Type</Th>
                <Th>Company</Th>
                <Th>Amount</Th>
                <Th>Expected Return %</Th>
                <Th>Date Invested</Th>
                <Th>Action</Th>
              </tr>
            </thead>
            <tbody>
              {investments.map(inv => (
                <tr key={inv._id}>
                  <Td>{inv.type}</Td>
                  <Td>{inv.company || "-"}</Td>
                  <Td>{inv.amount}</Td>
                  <Td>{inv.expected_return !== undefined ? inv.expected_return : "-"}</Td>
                  <Td>{formatDate(inv.date_invested)}</Td>
                  <Td><Button onClick={() => handleSell(inv._id)}>Sell</Button></Td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Card>
      </Container>
    </>
  );
}

export default Investment;
